const ToyotaClient = require('./index')
const client = new ToyotaClient()
const fs = require('fs')

client
    .getVehicle('Camry', 2017)
    .then(function(data) {
        fs.writeFileSync('examples/responses/getVehicle_with_year.json', JSON.stringify(data))
    })
    .catch(function(err) {
        console.log(err)
    })

client
    .getVehicle('Camry')
    .then(function(data) {
        fs.writeFileSync('examples/responses/getVehicle_without_year.json', JSON.stringify(data))
    })
    .catch(function(err) {
        console.log(err);
    })

client
    .getAllVehicles()
    .then(function(data) {
        fs.writeFileSync('examples/responses/getAllVehicles.json', JSON.stringify(data))
    })
    .catch(function(err) {
        console.log(err);
    })